// Write your Javascript code.
function decrementa(produtoId)
{
    var url = "/Produto/DecrementaEstoque";
    var param = {
        id : produtoId
    };
    $.post(url, param, function(retorno) {
        var elemento = $('#quantidade'+retorno.id);
        elemento.html(retorno.quantidade);
    });
}
function incrementa(produtoId)
{
    var url = "/Produto/IncrementaEstoque";
    var param = {
        id : produtoId
    };
    $.post(url, param, function(retorno) {
        var elemento = $('#quantidade'+retorno.id);
        elemento.html(retorno.quantidade);
    });
}
$('.decrementa').click(function() {
    var produtoId = $(this).data('id');
    decrementa(produtoId);
});
$('.incrementa').click(function() {
    var produtoId = $(this).data('id');
    incrementa(produtoId);
});